mapper.select:
/* kr.co.finmodeun.admin.admin.mapper.CmsAdminInfoMapper.selectCmsAdminInfoList */ SELECT admin.ADMIN_ID , admin.ADMIN_NAME , admin.ADMIN_TYPE , admin.HP_NO , admin.EMAIL_ADDR , admin.PWD_CHANGE_DT , admin.LOGIN_DT , admin.LOGIN_FAILUER_CNT , admin.LOGIN_FAILUER_DT , admin.USE_YN , admin_type.CODE_NAME AS ADMIN_TYPE_NAME FROM CMS_ADMIN_INFO admin JOIN CMN_CODE_INFO admin_type ON admin.ADMIN_TYPE = admin_type.CMN_CODE AND admin_type.PARENT_CODE = 'ADMIN_TYPE'
AND admin.ADMIN_ID LIKE '%' + #{adminId} + '%'
AND admin.ADMIN_NAME LIKE '%' + #{adminName} + '%'
AND admin.ADMIN_TYPE = #{adminType}
AND admin.USE_YN = #{useYn}

mapper.insert:
/* kr.co.finmodeun.admin.admin.mapper.CmsAdminInfoMapper.insertCmsAdminInfo */
INSERT INTO CMS_ADMIN_INFO (
    ADMIN_ID
    , ADMIN_PWD
    , ADMIN_NAME
    , ADMIN_TYPE
    , HP_NO

        , EMAIL_ADDR

    , PWD_CHANGE_DT
    , CRE_ID
    , CRE_DT
) VALUES (
    #{adminId}
    , #{adminPwd}
    , #{adminName}
    , #{adminType}
    , #{hpNo}

        , #{emailAddr}

    , CURRENT_TIMESTAMP
    , #{creId}
    , CURRENT_TIMESTAMP
)

mapper.update:
/* kr.co.finmodeun.admin.admin.mapper.CmsAdminInfoMapper.updateCmsAdminInfo */
UPDATE CMS_ADMIN_INFO


        , ADMIN_NAME = #{adminName}


        , ADMIN_TYPE = #{adminType}


        , HP_NO = #{hpNo}

    , EMAIL_ADDR = #{emailAddr}

        , USE_YN = #{useYn}

    , MOD_ID = #{modId}
    , MOD_DT = CURRENT_TIMESTAMP

WHERE
    ADMIN_ID = #{adminId}

/* kr.co.finmodeun.admin.admin.mapper.CmsAdminInfoMapper.updateAdminPwd */
UPDATE CMS_ADMIN_INFO
SET
    ADMIN_PWD = #{adminPwd}
    , PWD_CHANGE_DT = CURRENT_TIMESTAMP
WHERE
    ADMIN_ID = #{adminId}
