mapper.select:
/* kr.co.finmodeun.admin.code.mapper.CmnCodeInfoMapper.selectCmnCodeInfoList */ SELECT CMN_CODE , CODE_NAME , PARENT_CODE , SORT_ORDER , DISPLAY_YN FROM CMN_CODE_INFO
AND PARENT_CODE = #{parentCode}
AND DISPLAY_YN = #{displayYn}
AND CODE_NAME LIKE '%' + #{codeName} + '%'

mapper.insert:
/* kr.co.finmodeun.admin.code.mapper.CmnCodeInfoMapper.insertCmnCodeInfo */
INSERT INTO CMN_CODE_INFO (
    CMN_CODE
    , CODE_NAME
    , PARENT_CODE

        , CODE_DESC


        , SORT_ORDER


        , DISPLAY_YN

    , REG_ID
    , REG_DT
) VALUES (
    #{cmnCode}
    , #{codeName}
    , #{parentCode}

        , #{codeDesc}


        , #{sortOrder}


        , #{displayYn}

    , #{regId}
    , CURRENT_TIMESTAMP
)

mapper.update:
/* kr.co.finmodeun.admin.code.mapper.CmnCodeInfoMapper.updateCmnCodeInfo */
UPDATE CMN_CODE_INFO


        , CODE_NAME = #{codeName}

    , CODE_DESC = #{codeDesc}

        , SORT_ORDER = #{sortOrder}


        , DISPLAY_YN = #{displayYn}

    , MOD_ID = #{modId}
    , MOD_DT = CURRENT_TIMESTAMP

WHERE
    CMN_CODE = #{cmnCode}
    AND PARENT_CODE = #{parentCode}
