mapper.select:
/* mariadb.mapper.ColumnsMapper.selectColumnList */ SELECT t.TABLE_SCHEMA , t.TABLE_NAME , t.TABLE_COMMENT , c.COLUMN_NAME , c.COLUMN_DEFAULT , c.IS_NULLABLE , c.DATA_TYPE , c.COLUMN_TYPE , c.CHARACTER_MAXIMUM_LENGTH , c.CHARACTER_OCTET_LENGTH , c.NUMERIC_PRECISION , c.NUMERIC_SCALE , c.COLUMN_COMMENT , c.COLUMN_KEY , c.EXTRA FROM information_schema.TABLES t JOIN information_schema.COLUMNS c ON t.TABLE_NAME = c.TABLE_NAME WHERE t.TABLE_NAME = #{tableName}
AND t.TABLE_SCHEMA = #{tableSchema}
ORDER BY c.ORDINAL_POSITION
